import React, { useState } from "react";
import names from "./BabyNamesData.js";

const searchBar = () => {
  const [searchInput, setSearchInput] = useState("");
  // let filteredData = names.filter((baby) => {
  //   return baby.name.toLowerCase();
  // });
  const handleChange = (baby) => {
    baby.preventDefault();
    setSearchInput(baby.target.value);
  };

  if (searchInput.length > 0) {
    filteredData.filter((baby) => {
      return baby.name.match(searchInput);
    });
    return (
      <div>
        <input
          type="text"
          placeholder="Search here"
          onChange={handleChange}
          value={searchInput}
        />
        <ul>
          {filteredData.map((baby) => {
            <li>{baby.index}</li>;
          })}
        </ul>
      </div>
    );
  }
};

export default searchBar;
