import React, { useState } from "react";
import names from "./BabyNamesData";
const Button = () => {
  const [gender, setgender] = useState();
  const handlegender = () => {
    if (gender === "f") {
      setgender(
        gender.map((names) => {
          return {
            name: names.name,
          };
        })
      );
    }
    if (gender === "m") {
      setgender(
        gender.map(() => {
          return {
            name: names.sex,
          };
        })
      );
    }


    names = names.filter(function (el) {
      return (
        el.sex === "m" && el.sex === "f");
    });
    // setgender(e.target.value);
  };
  return (
    <div className="button-container">
      <button
        onChange={handlegender}
        name="gender"
        className="checked"
        type="radio"
      >
        <i class="fa-solid fa-user" id="girl"></i>
      </button>
      <button
        onChange={handlegender}
        name="gender"
        className="checked"
        type="radio"
      >
        <i class="fa-solid fa-user" id="boy"></i>
      </button>
      <button
        onChange={handlegender}
        name="gender"
        className="checked"
        type="radio"
      >
        <i class="fa-solid fa-user" id="mix"></i>
      </button>
    </div>
  );
};
export default Button;
