import React, { useState } from "react";
import names from "./BabyNamesData.js";

const getFilteredItems = (query, items) => {
  if (!query) {
    return items;
  }
  return items.filter((el) => el.name.includes(query));
};
const SearchBar = () => {
  const [query, setQuery] = useState("");
  let blueNames = names.sort(function (a, b) {
    names.sort((a, b) => {
      return a.name - b.name;
    });
    if (a.name < b.name) {
      return -1;
    }
    if (a.name > b.name) {
      return 1;
    }
    return 0;
  });
  // names.sort((a, b) => {
  //   return a.name - b.name;
  // });

  const sortedNames = getFilteredItems(query, names);
  return (
    <div>
      <label> Search:</label>
      <input type="text" onChange={(e) => setQuery(e.target.value)} />
      <ul>
        {sortedNames.map(({ name, sex }) => (
          <li key ={name}
            style={{
              backgroundColor: sex === "f" ? "#FFC6E5" : "#A0CFEE",
              color: sex === "f" ? "red" : "blue",
            }}
          >
            {name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchBar;
