        <i class="fa-solid fa-user" id="boy"></i>
      </button>
      <button
        onClick={() => handlegender("")}
        name="gender"
        className="checked"
      >
        <i class="fa-solid fa-user" id="mix"></i>
      </button>
    </div>
  );
};
export default Button;
