};
  return (

    <div className="button-container">
      <button onChange={handlegender}>
        <i class="fa-solid fa-user" id="girl"></i>
      </button>
      <button>
        <i class="fa-solid fa-user" id="boy"></i>
      </button>
      <button>
        <i class="fa-solid fa-user" id="mix"></i>
      </button>
    </div>
  );
};
export default Button;
